mapper.select:
select nvl(count(*),0) from studyboard

	WHERE num = #{num}


	WHERE boardid=#{boardid}

select * from (select rownum rnum ,a.* from (select num,writer,subject,passwd,
			reg_date,readcount,content
			 from studyboard where boardid = #{boardid} order by reg_date desc) 
			a ) where rnum  between #{startRow} and #{endRow}
